mapper.select:
select  size,males_total,females_total
from mice_total
where size = ${size}

mapper.insert:
insert into adult_mice (
registration_date, size, male_of_stock, female_of_stock
) values (
 current_date,
 ${size},
 ${male_of_stock},
 ${female_of_stock}
 )

mapper.update:
update adult_mice
set male_of_stock = ${male_of_stock},female_of_stock = ${female_of_stock}
where id = ${id}
